import Path from 'path'
import WpXml2Md from '../lib/index.js'

/**
 * Help text.
 * @type {String}
 */
const HelpText = `
Usage: wpxml2md [OPTIONS]

  Convert the WordPress XML file to Markdown files.

  Options:
    -h, --help            Display this text.
    -v, --version         Display the version number.
    -i, --input           Path of the XML file exported from WordPress.
    -o, --output          Path of the output directory.
    -r, --report          Output process reports.
    --no-gfm              Disable the GitHub Flavored Markdown.
    --no-melink           Disable the Markdown Extra link on header.
    --with-metadata       Enable output article metadata.
    --with-image-download Enable download and replace link syntaxes a linked images from article.
    --with-comment        Enable comment output from article.
    --replace-link-prefix Replace the link URL prefix with the specified word, format is "target=placeholder".
                          If "--replace-link http://example.com/=/" then "http://example.com/" will be replaced with "/".

  Examples:
    $ wpxml2md -i wordpress.xml -o ./dist -r
    $ wpxml2md -i wordpress.xml -o ./dist -r --with-metadata --with-image-download --with-comment --replace-link-prefix http://akabeko.me/=/

  See also:
    https://github.com/akabekobeko/npm-wpxml2md
`

/**
 * CLI options.
 * @type {Object}
 */
const Options = {
  help: { name: '--help', shortName: '-h' },
  version: { name: '--version', shortName: '-v' },
  input: { name: '--input', shortName: '-i' },
  output: { name: '--output', shortName: '-o' },
  report: { name: '--report', shortName: '-r' },
  noGFM: { name: '--no-gfm' },
  noMELink: { name: '--no-melink' },
  withMetadata: { name: '--with-metadata' },
  withImageDownload: { name: '--with-image-download' },
  withComment: { name: '--with-comment' },
  replaceLinkPrefix: { name: '--replace-link-prefix' }
}

/**
 * Check that it is an option value.
 *
 * @param {String} value Value.
 *
 * @return {Boolean} If the option of the value "true".
 */
const isValue = (value) => {
  const keys = Object.keys(Options)
  return !(keys.some((key) => value === Options[key].name || value === Options[key].shortName))
}

/**
 * Parse for option value.
 *
 * @param {String[]} argv Arguments of the command line.
 * @param {Number} index Index of argumens.
 *
 * @return {String} Its contents if the option value, otherwise null.
 */
const parseArgValue =  (argv, index) => {
  if (!(index + 1 < argv.length)) {
    return null
  }

  const value = argv[index + 1]
  return (isValue(value) ? value : null)
}

/**
 * Parse for the link option.
 *
 * @param {String} value Option value.
 *
 * @return {Object} Replace targets.
 */
const parseReplaceLinkURL = (value) => {
  const units = value.split('=')
  if (units.length < 2) {
    return { old: '', new: '' }
  }

  return { old: units[0], new: units[1] }
}

/**
 * Parse for the command line argumens.
 *
 * @param {String[]} argv Arguments of the command line.
 *
 * @return {CLIOptions} Parse results.
 */
const parseArgv = (argv) => {
  const options = {}
  let   value   = null

  argv.forEach((arg, index) => {
    switch (arg) {
      case Options.input.name:
      case Options.input.shortName:
        value = parseArgValue(argv, index)
        if (value) {
          options.input = Path.resolve(value)
        }
        break

      case Options.output.name:
      case Options.output.shortName:
        value = parseArgValue(argv, index)
        if (value) {
          options.output = Path.resolve(value)
        }
        break

      case Options.report.name:
      case Options.report.shortName:
        options.report = true
        break

      case Options.noGFM.name:
        options.noGFM = true
        break

      case Options.noMELink.name:
        options.noMELink = true
        break

      case Options.withMetadata.name:
        options.withMetadata = true
        break

      case Options.withImageDownload.name:
        options.withImageDownload = true
        break

      case Options.withComment.name:
        options.withComment = true
        break

      case Options.replaceLinkPrefix.name:
        value = parseArgValue(argv, index)
        if (value) {
          options.replaceLinkPrefix = parseReplaceLinkURL(value)
        }
        break

      default:
        break
    }
  })

  return options
}

/**
 * Parse for the command line arguments.
 *
 * @param {String[]} argv Arguments of the command line.
 *
 * @return {CLIOptions} Parse results.
 */
const parseOption = (argv) => {
  if (!(argv && 0 < argv.length)) {
    return { help: true }
  }

  switch (argv[0]) {
    case Options.help.name:
    case Options.help.shortName:
      return { help: true }

    case Options.version.name:
    case Options.version.shortName:
      return { version: true }

    default:
      return parseArgv(argv)
  }
}

/**
 * Print a help text.
 *
 * @param {WritableStream} stdout Standard output.
 */
const printHelp = (stdout) => {
  stdout.write(HelpText)
}

/**
 * Print a version number.
 *
 * @param {WritableStream} stdout Standard output.
 */
const printVersion = (stdout) => {
  const read = (path) => {
    try {
      return require(path).version
    } catch (err) {
      return null
    }
  }

  const version = read('../package.json') || read('../../package.json')
  stdout.write('v' + version + '\n')
}

/**
 * Entry point of the CLI.
 *
 * @param {String[]} argv Arguments of the command line.
 * @param {WritableStream} stdout Standard output.
 *
 * @return {Promise} Asynchronous task.
 */
const CLI = (argv, stdout) => {
  return new Promise((resolve, reject) => {
    const options = parseOption(argv)
    if (options.help) {
      printHelp(stdout)
      return resolve()
    }

    if (options.version) {
      printVersion(stdout)
      return resolve()
    }

    if (!(options.input)) {
      return reject(new Error('"-i" or "--input" has not been specified. This parameter is required.'))
    }

    if (!(options.output)) {
      return reject(new Error('"-o" or "--output" has not been specified. This parameter is required.'))
    }

    return WpXml2Md(options.input, options.output, options)
  })
}

export default CLI

#!/usr/bin/env node

import CLI from './cli.js'

CLI(process.argv.slice(2), process.stdout)
  .catch((err) => {
    console.error(err)
  })

import MD5 from 'md5'
import Util from './util.js'

/**
 * Create a Markdown image link (Gravatar) from e-mail address.
 * @param {String} author Author.
 * @param {String} mail Mail e-mail address.
 * @return {String} Markdown text.
 */
const createIconLink = (author, mail) => {
  return mail ? `![${author}](https://www.gravatar.com/avatar/${MD5(mail)}?d=identicon) ` : ''
}

/**
 * Create a Markdown text of author.
 * @param {String} author Author.
 * @param {String} url URL of author web site.
 * @return {String} Markdown text.
 */
const createAuthor = (author, url) => {
  return url ? `[${author}](${url})` : author
}

/**
 * Create a datetime text.
 * @param {Object} date Datetime
 * @return {String} Markdown text.
 */
const createDate = (date) => {
  return date ? `${date.year}-${date.month}-${date.day}T${date.time}Z` : ''
}

/**
 * Convert a content of comment to indented markdown code block.
 * @param {String} content Content of comment.
 * @return markdown code block.
 */
const convertBody = (content) => {
  return content.replace(/^/mg, '  > ')
}

/**
 * Convert a comments to Markdown.
 * @param {Object[]} comments Comments.
 * @return {String} Markdown text.
 */
const convertMarkdown = (comments) => {
  if (!(comments && 0 < comments.length)) {
    return ''
  }

  let md = ''
  for (let comment of comments) {
    const body = convertBody(comment.content)
    md += `* ${createIconLink(comment.author, comment.mail)}**${createAuthor(comment.author, comment.url)}** ${createDate(comment.date)}\n${body}\n`
    if (comment.children) {
      md += convertMarkdown(comment.children)
    }
  }

  return md
}

/**
 * Crerate a comment tree with parent identifier.
 * @param {Object[]} comments Comments.
 * @return {Object[]} Parsed comments.
 */
const createCommentTree = (comments) => {
  const tree = []
  if (!(Array.isArray(comments))) {
    return tree
  }

  // Identifier mapping and deep copy
  const map = {}
  const list = []
  for (let i = 0; i < comments.length; ++i) {
    const comment = comments[i]
    map[comment.id] = i
    list.push(Object.assign({}, comment))
  }

  for (let i = 0; i < list.length; ++i) {
    const comment = list[i]
    if (comment.parent && comment.parent !== '0') {
      const parent = list[map[comment.parent]]
      if (parent.children) {
        parent.children.push(comment)
      } else {
        parent.children = [comment]
      }
    } else {
      // Root
      tree.push(comment)
    }
  }

  return tree
}

/**
 * Parse a comments..
 * @param {Object[]} src Comments (wp:comment).
 * @return {Object[]} Parsed comments.
 */
const parse = (src) => {
  const comments = []
  if (!(Array.isArray(src))) {
    return comments
  }

  for (let comment of src) {
    comments.push({
      id: comment['wp:comment_id'][0],
      author: comment['wp:comment_author'][0],
      mail: comment['wp:comment_author_email'][0],
      url: comment['wp:comment_author_url'][0],
      date: Util.datetimeFromWpGMT(comment['wp:comment_date_gmt'][0]),
      content: comment['wp:comment_content'][0],
      parent: comment['wp:comment_parent'][0]
    })
  }

  return comments
}

/**
 * Create a comment list.
 * @param {Object[]} src Comments (wp:comment).
 * @return {String} Comment list (HTML text).
 */
const Comment = (src) => {
  const comments = createCommentTree(parse(src))
  if (comments.length === 0) {
    return ''
  }

  return `\n\n## Comments from WordPress\n\n${convertMarkdown(comments)}`
}

export default Comment

import { JSDOM } from 'jsdom'
import CollapseWhitespace from 'collapse-whitespace'
import Util from './util.js'
import ConvertShortCode from './shortcode.js'
import MarkdownConverters from './markdown.js'
import GfmConverters from './gfm.js'

/**
 * Types of node.
 * @type {Object}
 * @see https://developer.mozilla.org/en-US/docs/Web/API/Node/nodeType
 */
const NodeTypes = {
  ELEMENT_NODE: 1,
  TEXT_NODE: 3
}

/**
 * RegExp.
 * @type {Object}
 */
const RegExps = {
  Alphabet: /A/,
  Space: /^\s*$/i,
  Leading: /^[ \r\n\t]/,
  Trailing: /[ \r\n\t]$/
}

/**
 * Check that conversion is possible.
 *
 * @param {Node} node DOM node.
 * @param {String|String[]|Function} filter Filter.
 *
 * @return {Boolean} "true" if the conversion is possible.
 */
const canConvert = (node, filter) => {
  if (typeof filter === 'string') {
    return (filter === node.nodeName.toLowerCase())
  }

  if (Array.isArray(filter)) {
    return (filter.indexOf(node.nodeName.toLowerCase()) !== -1)
  } else if (typeof filter === 'function') {
    return filter(node)
  }

  throw new TypeError('"filter" needs to be a string, array, or function')
}

/**
 * Collapse the whitespace from ELEMENT_NODE node.
 * TEXT_NODE will keep the original indentation and whitespace.
 *
 * @param {Node[]} nodes DOM nodes.
 */
const collapseWhitespace = (nodes) => {
  nodes.forEach((node) => {
    if (node.nodeType === NodeTypes.ELEMENT_NODE) {
      CollapseWhitespace(node, Util.isBlockElement)
    }
  })
}

/**
 * Flanking the whitespaces.
 *
 * @param {Node} node DOM node.
 *
 * @return {Object} whitespaces.
 */
const flankingWhitespace = (node) => {
  let leading  = ''
  let trailing = ''

  if (!(Util.isBlockElement(node))) {
    const hasLeading  = RegExps.Leading.test(node.innerHTML)
    const hasTrailing = RegExps.Trailing.test(node.innerHTML)
    if (hasLeading && !(isFlankedByWhitespace('left', node))) {
      leading = ' '
    }

    if (hasTrailing && !(isFlankedByWhitespace('right', node))) {
      trailing = ' '
    }
  }

  return { leading: leading, trailing: trailing }
}

/**
 * Flattens the tree structure of nodes.
 *
 * @param {Node} node DOM node.
 *
 * @return {Node[]} Nodes.
 */
const flattenNodes = (node) =>  {
  const inqueue  = [node]
  const outqueue = []

  while (0 < inqueue.length) {
    const elm = inqueue.shift()
    outqueue.push(elm)

    for (let i = 0, max = elm.childNodes.length; i < max; ++i) {
      const child = elm.childNodes[i]
      if (child.nodeType === NodeTypes.ELEMENT_NODE) {
        inqueue.push(child)
      }
    }
  }

  // Remove root
  outqueue.shift()

  return outqueue
}

/**
 * Get a child contents text.
 *
 * @param {Node} node DOM node.
 *
 * @return {String} Text.
 */
const getContent = (node) => {
  let text = ''
  for (let i = 0, max = node.childNodes.length; i < max; ++i) {
    const elm = node.childNodes[i]
    if (elm.nodeType === NodeTypes.ELEMENT_NODE) {
      text += node.childNodes[i]._replacement
    } else if (elm.nodeType === NodeTypes.TEXT_NODE) {
      text += elm.data
    }
  }

  return text
}

/**
 * Prepare the text for parse the jsdom.
 *
 * @param {String} text Text.
 *
 * @return {String} Prepared text.
 */
const prepareText = (text) => {
  const result = ConvertShortCode(text)

  // Escape number list
  return result.replace(/(\d+)\. /g, '$1\\. ')
}

/**
 * Check a flanked by whitespace.
 *
 * @param {String} side
 * @param {Node} node Node.
 *
 * @return {Boolean} Flanked if "true".
 */
const isFlankedByWhitespace = (side, node) => {
  let sibling = null
  let regexp  = null
  if (side === 'left') {
    sibling = node.previousSibling
    regexp  = / $/
  } else {
    sibling = node.nextSibling
    regexp  = /^ /
  }

  let isFlanked = false
  if (sibling) {
    if (sibling.nodeType === NodeTypes.TEXT_NODE) {
      isFlanked = regexp.test(sibling.nodeValue)
    } else if (sibling.nodeType === NodeTypes.ELEMENT_NODE && !(Util.isBlockElement(sibling))) {
      isFlanked = regexp.test(sibling.textContent)
    }
  }

  return isFlanked
}

/**
 * Convert the Node to Markdown text.
 *
 * @param {Node} node DOM node.
 * @param {Converter[]} converters Converters.
 * @param {Modes} modes Modes.
 */
const process = (node, converters, modes) => {
  let content = getContent(node)

  // Remove blank nodes
  if (!(Util.isVoidElement(node)) && RegExps.Alphabet.test(node.nodeName) && RegExps.Space.test(content)) {
    node._replacement = ''
    return
  }

  let replacement = ''
  converters.some((converter) => {
    if (!(canConvert(node, converter.filter))) {
      return false
    }

    if (typeof converter.replacement !== 'function') {
      throw new TypeError('"replacement" needs to be a function that returns a string')
    }

    const whitespace = flankingWhitespace(node)
    if (whitespace.leading || whitespace.trailing) {
      content = Util.trim(content)
    }

    replacement = whitespace.leading +
                  converter.replacement(node, content, modes) +
                  whitespace.trailing

    return true
  })

  node._replacement = replacement
}

/**
 * Convert the WordPress's post to Markdown.
 * Design and implementation was in reference to the npm to-markdown.
 *
 * @param {String} post WordPress's post text.
 * @param {Modes} modes Modes.
 *
 * @return {String} Markdown text.
 *
 * @see https://github.com/domchristie/to-markdown
 */
const Convert = (post, modes = {}) => {
  if (typeof post !== 'string') {
    throw new TypeError('"post" is not a string.')
  }

  let converters = MarkdownConverters.slice(0)
  if (!(modes.noGFM)) {
    converters = GfmConverters.concat(converters)
  }

  const body  = (new JSDOM(prepareText(post))).window.document.body
  const nodes = flattenNodes(body)
  collapseWhitespace(nodes)

  // Process through nodes in reverse (so deepest child elements are first).
  for (let i = nodes.length - 1; 0 <= i; --i) {
    process(nodes[i], converters, modes)
  }

  const result = getContent(body)
  return result.replace(/^[\t\r\n]+|[\t\r\n\s]+$/g, '')
    .replace(/\n\s+\n/g, '\n\n')
    .replace(/\n{3,}/g, '\n\n')
}

export default Convert

import Util from './util.js'

/**
 * Convert the DOM node to cell text of the table.
 *
 * @param {Node} node DOM node.
 * @param {String} content Content text.
 *
 * @return {String} Cell text.
 */
function Cell (node, content) {
  let index = 0
  if (node.parentNode && node.parentNode.childNodes) {
    index = Util.arrayIndexOf(node.parentNode.childNodes, node)
  }

  const prefix = (index === 0 ? '| ' : ' ')
  return prefix + content + ' |'
}

/**
 * RegExp for the higlighting.
 * @type {RegExp}
 */
const HighlightRegEx = /highlight highlight-(\S+)/

/**
 * Convert the HTML DOM Node to GFM (GitHub Flavored Markdown) text.
 * @type {Array.<MdConverter>}
 * @see https://github.com/domchristie/to-markdown/blob/master/lib/gfm-converters.js
 */
const GfmConverters = [
  // Line break
  {
    filter: 'br',
    replacement: () => {
      return '\n'
    }
  },
  // Delete
  {
    filter: ['del', 's', 'strike'],
    replacement: (node, content) => {
      return '~~' + content + '~~'
    }
  },
  // Checkbox
  {
    filter: (node) => {
      return node.type === 'checkbox' && node.parentNode.nodeName === 'LI'
    },
    replacement: (node) => {
      return (node.checked ? '[x]' : '[ ]') + ' '
    }
  },
  // Table cell
  {
    filter: [ 'th', 'td' ],
    replacement: (node, content) => {
      return Cell(node, content)
    }
  },
  // Table row
  {
    filter: 'tr',
    replacement: (node, content) => {
      let   borderCells = ''
      const alignMap    = { left: ':--', right: '--:', center: ':-:' }

      if (node.parentNode.nodeName === 'THEAD') {
        for (let i = 0, max = node.childNodes.length; i < max; ++i) {
          const elm    = node.childNodes[i]
          const align  = (elm.attributes ? elm.attributes.align : null)
          const border = (align ? alignMap[ align.value ] : '---')

          if (elm._replacement) {
            borderCells += Cell(elm, border)
          }
        }
      }

      return '\n' + content + (borderCells ? '\n' + borderCells : '')
    }
  },
  // Table
  {
    filter: 'table',
    replacement: (node, content) => {
      return '\n\n' + content + '\n\n'
    }
  },
  // Table parts
  {
    filter: [ 'thead', 'tbody', 'tfoot' ],
    replacement: (node, content) => {
      return content
    }
  },
  // Fenced code blocks
  {
    filter: (node) => {
      return node.nodeName === 'PRE' &&
             node.firstChild &&
             node.firstChild.nodeName === 'CODE'
    },
    replacement: (node) => {
      return '\n\n```\n' + node.firstChild.textContent + '\n```\n\n'
    }
  },
  // Syntax-highlighted code blocks
  {
    filter: (node) => {
      return node.nodeName === 'PRE' &&
             node.parentNode.nodeName === 'DIV' &&
             HighlightRegEx.test(node.parentNode.className)
    },
    replacement: (node) => {
      const language = node.parentNode.className.match(HighlightRegEx)[ 1 ]
      return '\n\n```' + language + '\n' + node.textContent + '\n```\n\n'
    }
  },
  // Div
  {
    filter: (node) => {
      return node.nodeName === 'DIV' &&
             HighlightRegEx.test(node.className)
    },
    replacement: (node, content) => {
      return '\n\n' + content + '\n\n'
    }
  }
]

export default GfmConverters

import Fs from 'fs'
import Path from 'path'
import NodeUtil from 'util'
import Request from 'request'
import Util from './util.js'

const RequestGet = NodeUtil.promisify(Request)

/**
 * Regular expression.of an image link.
 *
 * @type {RegExp}
 */
const REGEX_IMAGE_LINK = /(\[!\[[^\]]*\]\((.*?)\s*("(?:.*[^"])")?\s*\)\]\((.*?)\s*("(?:.*[^"])")?\s*\))|!\[[^\]]*\]\((.*?)\s*("(?:.*[^"])")?\s*\)/g

/**
 * Regular expression.of an image URL.
 *
 * @type {RegExp}
 */
const REGEX_IMAGE_URL = /(http)?s?:?(\/\/[^"']*?\.(?:png|jpg|jpeg|gif|png|svg))/

/**
 * Recursively enumurate a file paths from directory.
 *
 * @param {String} dir Path of root directory.
 *
 * @return {String[]} File paths.
 */
const enumFiles = (dir) => {
  let results = []
  const items = Fs.readdirSync(dir)

  items.forEach((item) => {
    const path = Path.join(dir, item)
    const stat = Fs.statSync(path)

    if (stat.isDirectory()) {
      results = results.concat(enumFiles(path))
    } else {
      results.push(path)
    }
  })

  return results
}

/**
 * Download an images.
 *
 * @param {Object} images Image URL/Name.
 * @param {String} dir Save directory.
 * @param {Logger} logger Logger.
 *
 * @return {Promise} Asynchronous task.
 */
const downloadImages = async (images, dir, logger) => {
  const results = []
  for (let image of images) {
    logger.log(`Download: "${image.url}" => "${image.fileName}"`)

    try {
      const { error, response, body } = await RequestGet({ method: 'GET', url: image.url, encoding: null })
      if (error) {
        throw error
      } else if (response && response.statusCode !== 200) {
        throw new Error(`ERROR: status code ${response.statusCode}`)
      }

      Fs.writeFileSync(Path.join(dir, image.fileName), body, 'binary')
      results.push(image)
    } catch (err) {
      logger.error(err)
    }
  }

  return results
}

/**
 * Get image link and URL list from Markdown.
 *
 * @param {String} markdown Markdown text.
 * @param {String} basename Name on which to base the saved image file name.
 *
 * @return {Object} Link and image (URL/Saved file name) list.
 */
const parseImageLink = (markdown, basename) => {
  if (!(markdown)) {
    return { links: [], images: [] }
  }

  const urls = []
  const links = markdown.match(REGEX_IMAGE_LINK)
  if (!(links && 0 < links.length)) {
    return { links: [], images: [] }
  }

  links.forEach((link) => {
    link.replace(REGEX_IMAGE_URL, (url) => {
      urls.push(url)
    })
  })

  return {
    links,
    images: urls
      .filter((url, i, arr) => arr.indexOf(url) === i)
      .map((url, i) => {
        return {
          url,
          fileName: `${basename}-${i + 1}${Path.extname(url)}`
        }
      })
  }
}

/**
 * Replace a link syntaxes.
 *
 * @param {String[]} links Link syntaxes in markdown.
 * @param {Object[]} images Image URL/Name.
 *
 * @return {Object[]} Replaced link syntaxes.
 */
const replaceLinks = (links, images) => {
  const results = []
  for (let link of links) {
    let newLink = link
    for (let image of images) {
      const regexp = new RegExp(Util.escapeRegExp(image.url), 'g')
      newLink = newLink.replace(regexp, image.fileName)
    }

    if (newLink !== link) {
      // Make it a replacement candidate for markdown if it is replaced
      results.push({ link, newLink })
    }
  }

  return results
}

/**
 * Download the linked image from Markdown and rewrite the link.
 *
 * @param {String} markdown Markdown text.
 * @param {String} dir Directory where Markdown was output.
 * @param {String} basename Name to be the base of the image file to be saved.
 * @param {Logger} logger Logger.
 *
 * @return {Promise} Asynchronous task.
 */
const ImageLinkReplacer = async (markdown, dir, basename, logger) => {
  try {
    let data = parseImageLink(markdown, basename)
    if (data.images.length === 0) {
      return markdown
    }

    const succeededImages = await downloadImages(data.images, dir, logger)
    const targets = replaceLinks(data.links, succeededImages)

    for (let target of targets) {
      markdown = markdown.replace(target.link, target.newLink)
    }
  } catch (err) {
    logger.error(err)
  }

  return markdown
}

export default ImageLinkReplacer

/**
 * Display the log message for the stdout.
 */
export default class Logger {
  /**
   * Initialize instance.
   *
   * @param {Boolean} available "true" to display the report, default is "true".
   */
  constructor (available = true) {
    if (available === null || available === undefined) {
      available = true
    }

    this._available = available
  }

  /**
   * Display a log message for the stdout.
   *
   * @param {Object[]} args Message arguments.
   */
  log (...args) {
    if (this._available) {
      console.log(...args)
    }
  }

  /**
   * Display an error message for the stdout.
   *
   * @param {Object[]} args Message arguments.
   */
  error (...args) {
    if (this._available) {
      console.error(...args)
    }
  }
}

import Util from './util.js'

/**
 * Convert the HTML DOM Node to Markdown text.
 * @type {MdConverter[]}
 * @see https://github.com/domchristie/to-markdown/blob/master/lib/md-converters.js
 */
const MarkdownConverters = [
  // Paragraph
  {
    filter: 'p',
    replacement: (node, content) => {
      return '\n\n' + content + '\n\n'
    }
  },
  // Line break
  {
    filter: 'br',
    replacement: () => {
      return '  \n'
    }
  },
  // Header
  {
    filter: [ 'h1', 'h2', 'h3', 'h4', 'h5', 'h6' ],
    replacement: (node, content, modes) => {
      const level  = node.nodeName.charAt(1)
      let   prefix = ''
      for (let i = 0; i < level; ++i) {
        prefix += '#'
      }

      // Inter link (Markdown Extra)
      if (node.id && !(modes.noMELink)) {
        return '\n\n' + prefix + ' ' + content + ' {#' + node.id + '}\n\n'
      }

      return '\n\n' + prefix + ' ' + content + '\n\n'
    }
  },
  // Line
  {
    filter: 'hr',
    replacement: () => {
      return '\n\n* * *\n\n'
    }
  },
  // Italic
  {
    filter: ['em', 'i'],
    replacement: (node, content) => {
      return '_' + content + '_'
    }
  },
  // Strong
  {
    filter: [ 'strong', 'b' ],
    replacement: (node, content) => {
      return '**' + content + '**'
    }
  },
  // Inline code
  {
    filter: (node) => {
      const hasSiblings = node.previousSibling || node.nextSibling
      const isCodeBlock = (node.parentNode.nodeName === 'PRE' && !(hasSiblings))

      return (node.nodeName === 'CODE' && !(isCodeBlock))
    },
    replacement: (node, content) => {
      return '`' + content + '`'
    }
  },
  // Link
  {
    filter: (node) => {
      return (node.nodeName === 'A' && node.getAttribute('href'))
    },
    replacement: (node, content) => {
      const titlePart = node.title ? ' "' + node.title + '"' : ''
      return '[' + content + '](' + node.getAttribute('href') + titlePart + ')'
    }
  },
  // Image
  {
    filter: 'img',
    replacement: (node) => {
      const alt       = node.alt || ''
      const src       = node.getAttribute('src') || ''
      const title     = node.title || ''
      const titlePart = title ? ' "' + title + '"' : ''

      return src ? '![' + alt + '](' + src + titlePart + ')' : ''
    }
  },
  // Code blocks
  {
    filter: (node) => {
      return node.nodeName === 'PRE' && node.firstChild.nodeName === 'CODE'
    },
    replacement: (node) => {
      return '\n\n    ' + node.firstChild.textContent.replace(/\n/g, '\n    ') + '\n\n'
    }
  },
  // Block quote
  {
    filter: 'blockquote',
    replacement: (node, content) => {
      let result = Util.trim(content)
      result = result.replace(/\n{3,}/g, '\n\n')
      result = result.replace(/^/gm, '> ')
      return '\n\n' + result + '\n\n'
    }
  },
  // List item
  {
    filter: 'li',
    replacement: (node, content) => {
      const text   = content.replace(/^\s+/, '').replace(/\n/gm, '\n    ')
      const parent = node.parentNode
      const index  = Util.arrayIndexOf(parent.children, node) + 1
      const ol     = /ol/i
      const prefix = ol.test(parent.nodeName) ? index + '. ' : '* '
      return prefix + text
    }
  },
  // List
  {
    filter: [ 'ul', 'ol' ],
    replacement: (node) => {
      const strings = []
      for (let i = 0, max = node.childNodes.length; i < max; ++i) {
        if (node.childNodes[i]._replacement) {
          strings.push(node.childNodes[i]._replacement)
        }
      }

      const li = /li/i
      if (li.test(node.parentNode.nodeName)) {
        return '\n' + strings.join('\n')
      }

      return '\n\n' + strings.join('\n') + '\n\n'
    }
  },
  // Block element
  {
    filter: (node) => {
      return Util.isBlockElement(node)
    },
    replacement: (node, content) => {
      return '\n\n' + Util.outerHTML(node, content) + '\n\n'
    }
  },
  // Anything else!
  {
    filter: () => {
      return true
    },
    replacement: (node, content) => {
      return Util.outerHTML(node, content)
    }
  }
]

export default MarkdownConverters

/**
 * Constatns for the Converter class.
 * @type {Object}
 */
const SHCodes = [
  // ``` + lang parameter
  'code',

  // ```
  'plain',
  'text',

  // ```lang
  'as3',
  'actionscript3',
  'bash',
  'shell',
  'cf',
  'coldfusion',
  'c-sharp',
  'csharp',
  'cpp',
  'c',
  'css',
  'delphi',
  'pas',
  'pascal',
  'diff',
  'patch',
  'erl',
  'erlang',
  'groovy',
  'js',
  'jscript',
  'javascript',
  'java',
  'jfx',
  'javafx',
  'perl',
  'pl',
  'php',
  'ps',
  'powershell',
  'py',
  'python',
  'rails',
  'ror',
  'ruby',
  'scala',
  'sql',
  'vb',
  'vbnet',
  'xml',
  'xhtml',
  'xslt',
  'html'
]

/**
 * String of the Code block.
 * @type {Object}
 */
const Block = {
  BeginPlain: '\n\n```\n',
  Begin: '\n\n```',
  End: '\n```\n\n'
}

/**
 * Remove the new line beginning and end of the code block.
 *
 * @param {String} text Code block text.
 *
 * @return {Text} New text.
 */
const trimLineBreak = (text) => {
  return text.replace(/^[\n]|[\n]$/g, '')
}

/**
 * Convert a SyntaxHighlighter shortcode to Markdown.
 *
 * @param {String} src Original text.
 * @param {String} code Shortcode name.
 * @param {Object} params Shortcode parameters.
 * @param {String} body Shortcode body.
 *
 * @return {String} Converted text.
 */
const convertSH = (src, code, params, body) => {
  const value = trimLineBreak(body)
  if (code === SHCodes[ 0 ]) {
    if (params.lang) {
      return Block.Begin + params.lang + '\n' + value + Block.End
    }

    return Block.BeginPlain + value + Block.End
  } else if (code === SHCodes[1] || code === SHCodes[2]) {
    return Block.BeginPlain + value + Block.End
  }

  for (let i = 3, max = SHCodes.length; i < max; ++i) {
    if (code === SHCodes[i]) {
      return Block.Begin + code + `\n` + value + Block.End
    }
  }

  return src
}

/**
 * Parse a WordPress shortcode ("code param1="value1" param2="value2").
 *
 * @param {String} text shortcode text.
 *
 * @return {Object} Parsed result.
 */
const parse = (text) => {
  const codes = text.split(' ')
  const obj   = { code: codes[0], params: {} }

  for (let i = 1, max = codes.length; i < max; ++i) {
    const params = codes[i].split('=')
    if (params.length === 1) {
      obj.params[params[0]] = ' '
    } else {
      obj.params[params[0]] = params[1].replace(/^"(.+(?="$))"$/, '$1')
    }
  }

  return obj
}

/**
 * Convert the WordPress's shotcode to Markdown.
 *
 * @return {String} Converted string.
 */
const ConvertShortCode = (text) => {
  return text.replace(/\[([^\]]+)]([^[]+)\[\/([^\]]+)]/igm, (src, $1, $2) => {
    const shortcode = parse($1)
    if (shortcode.code === 'caption') {
      return $2
    }

    return convertSH(src, shortcode.code, shortcode.params, $2)
  })
}

export default ConvertShortCode

import Fs from 'fs'
import Path from 'path'

/**
 * Elements of block.
 * @type {Array}
 */
const BlockElements = [
  'address', 'article', 'aside', 'audio', 'blockquote', 'body', 'canvas',
  'center', 'dd', 'dir', 'div', 'dl', 'dt', 'fieldset', 'figcaption',
  'figure', 'footer', 'form', 'frameset', 'h1', 'h2', 'h3', 'h4', 'h5', 'h6',
  'header', 'hgroup', 'hr', 'html', 'isindex', 'li', 'main', 'menu', 'nav',
  'noframes', 'noscript', 'ol', 'output', 'p', 'pre', 'section', 'table',
  'tbody', 'td', 'tfoot', 'th', 'thead', 'tr', 'ul'
]

/**
 * Elements of void.
 * @type {Array.<String>}
 */
const VoidElements = [
  'area', 'base', 'br', 'col', 'command', 'embed', 'hr', 'img', 'input',
  'keygen', 'link', 'meta', 'param', 'source', 'track', 'wbr'
]

/**
 * Provides utility function.
 */
export default class Util {
  /**
   * This method returns the first index at which a given element can be found in the array
   *
   * @param {Array} arr Array.
   * @param {Object} obj Element to locate in the array.
   *
   * @return {Number} If the success index, otherwise -1.
   */
  static arrayIndexOf (arr, obj) {
    return Array.prototype.indexOf.call(arr, obj)
  }

  /**
   * Escape a regexp syntaxes.
   *
   * @param {String} str Original string.
   *
   * @return {String} Escaped string.
   *
   * @see https://stackoverflow.com/questions/1144783/how-to-replace-all-occurrences-of-a-string-in-javascript
   */
  static escapeRegExp (str) {
    return str.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')
  }

  /**
   * Check the existence of a file or folder.
   *
   * @param {String} path Path of the file or folder.
   *
   * @return {Boolean} True if exists. Otherwise false.
   */
  static existsSync (path) {
    try {
      Fs.accessSync(Path.resolve(path), Fs.F_OK)
      return true
    } catch (err) {
      return false
    }
  }

  /**
   * Converts the value of the Date object to its equivalent string representation using the specified format.
   *
   * @param {Date}   date   Date and time. Default is current date and time.
   * @param {String} format Date and time format string. Default is "YYYY-MM-DD hh:mm:ss.SSS".
   *
   * @return {String} Formatted string.
   *
   * @see http://qiita.com/osakanafish/items/c64fe8a34e7221e811d0
   */
  static formatDate (date, format) {
    if (!(date)) {
      date = new Date()
    }

    if (typeof format !== 'string') {
      format = 'YYYY-MM-DD hh:mm:ss.SSS'
    }

    const Y = date.getFullYear()
    const M = date.getMonth()
    const D = date.getDate()
    const h = date.getHours()
    const m = date.getMinutes()
    const s = date.getSeconds()

    if (Number.isNaN(Y) || Number.isNaN(M) || Number.isNaN(D) || Number.isNaN(h) || Number.isNaN(m) || Number.isNaN(s)) {
      return null
    }

    // Zero padding
    let f = format
    f = f.replace(/YYYY/g, Y)
    f = f.replace(/MM/g,   ('0' + (M + 1)).slice(-2))
    f = f.replace(/DD/g,   ('0' +         D).slice(-2))
    f = f.replace(/hh/g,   ('0' +         h).slice(-2))
    f = f.replace(/mm/g,   ('0' +         m).slice(-2))
    f = f.replace(/ss/g,   ('0' +         s).slice(-2))

    // Single digit
    f = f.replace(/M/g, M + 1)
    f = f.replace(/D/g, D)
    f = f.replace(/h/g, h)
    f = f.replace(/m/g, m)
    f = f.replace(/s/g, s)

    if (f.match(/S/g)) {
      let ms = date.getMilliseconds()
      if (!(Number.isNaN(ms))) {
        ms = ('00' + ms).slice(-3)
        for (let i = 0, max = f.match(/S/g).length; i < max; ++i) {
          f = f.replace(/S/, ms.substring(i, i + 1))
        }
      }
    }

    return f
  }

  /**
   * Check the node of a block element.
   *
   * @param {Node} node Node.
   *
   * @return {Boolean} Block element if "true".
   */
  static isBlockElement (node) {
    return BlockElements.indexOf(node.nodeName.toLowerCase()) !== -1
  }

  /**
   * Check the node of a void element.
   *
   * @param {Node} node Node.
   *
   * @return {Boolean} Void element if "true".
   */
  static isVoidElement (node) {
    return VoidElements.indexOf(node.nodeName.toLowerCase()) !== -1
  }

  /**
   * Asynchronous mkdir(2). No arguments other than a possible exception are given to the completion callback.
   * mode defaults to 0o777.
   *
   * @param {String} path Directory path.
   *
   * @return {Boolean} Success if "true".
   */
  static mkdirSync (path) {
    const dir = Path.resolve(path)
    if (Util.existsSync(dir)) {
      return true
    }

    Fs.mkdirSync(dir)
    return Util.existsSync(dir)
  }

  /**
   * Get the HTML string of an element with its contents converted.
   *
   * @param  {Node}   node    DOM node.
   * @param  {String} content Text.
   *
   * @return {String} HTML text.
   */
  static outerHTML (node, content) {
    const clone = node.cloneNode(false)
    if (clone.outerHTML) {
      return clone.outerHTML.replace('><', '>' + content + '<')
    }

    return content
  }

  /**
   * Get a datetime from WordPress XML GMT
   *
   * @param {String} gmt String of GMT.
   *
   * @return {Object} Datetime.
   */
  static datetimeFromWpGMT (gmt) {
    const datetime = gmt.split(' ')
    const date     = datetime[0].split('-')

    return {
      year: date[0],
      month: date[1],
      day: date[2],
      time: datetime[1]
    }
  }

  /**
   * Remove whitespace from both sides of a string.
   *
   * @param {String} str String.
   *
   * @return {String} New string.1
   */
  static trim (str) {
    return str.replace(/^[ \r\n\t]+|[ \r\n\t]+$/g, '')
  }

  /**
   * If the file or folder to the same path on exists, generates a unique path that does not duplicate.
   * e.g. "./test" to "test-1", "./test.md" to "./test-1.md"
   *
   * @param  {String} path Original path.
   * @param  {Number} min  The minimum value of the sequential number. Defailt is 1.
   * @param  {Number} max  The maximum value of the sequential number. Defailt is 256.
   *
   * @return {String} Success is the unique path (full path), failure is null. If not duplicate returns the original path.
   */
  static uniquePathWithSequentialNumber (path, min = 1, max = 256) {
    const originalPath = Path.resolve(path)
    if (!(Util.existsSync(originalPath) && typeof min === 'number' && typeof max === 'number' && 0 <= min && min < max)) {
      return originalPath
    }

    const ext    = Path.extname(originalPath)
    const base   = Path.basename(originalPath, ext)
    const parent = Path.dirname(originalPath)

    for (let i = min; i <= max; ++i) {
      const name       = base + '-' + i + ext
      const uniquePath = Path.join(parent, name)
      if (!(Util.existsSync(uniquePath))) {
        return uniquePath
      }
    }

    return null
  }
}

import Fs from 'fs'
import Path  from 'path'
import NodeUtil from 'util'
import XmlParser  from 'xml2js'
import Util from './util.js'
import Logger from './logger.js'
import Convert from './converter.js'
import ImageLinkReplace from './image-link-replacer.js'
import Comment from './comment.js'

const ParseXML = NodeUtil.promisify(XmlParser.parseString)

/**
 * Create a directory to save the markdown file.
 * @param {String} root Path of the roo directory.
 * @param {String} year Year.
 * @param {String} month Month
 * @return {String} If successful it is the path of the created directory.
 */
const createSaveDir = (root, year, month) => {
  // root/year
  let dir  = Path.join(root, year)
  if (!(Util.mkdirSync(dir))) {
    return null
  }

  // root/year/month
  dir = Path.join(dir, month)
  if (!(Util.mkdirSync(dir))) {
    return null
  }

  return dir
}

/**
 * Array to string for metadata.
 * @param {Array} arr Array.
 * @return {String} String.
 */
const arrayToString = (arr) => {
  if (!(arr && arr.length)) {
    return '[]'
  }

  let str = `["${arr[0]}"`
  for (let i = 1, max = arr.length; i < max; ++i) {
    str += `, "${arr[i]}"`
  }

  str += ']'
  return str
}

/**
 * Create a excerpt string from Markdown text.
 * The specification of the excerpt statement is below.
 * - No line break
 * - No header, list (ul/ol), table, blockquote
 * - No Markdown decoration
 * - Markdown links and images extracted only text
 * - Add "..." to the end if it exceeds 100 characters
 * - Escape a double quote for YAML
 * @param {String} markdown Markdown text of content body.
 * @return {String} Excerpted string.
 */
const createExcerpt = (markdown) => {
  if (!markdown) {
    return ''
  }

  let str = markdown
    .replace(/\n\n/mg, '\n')
    .replace(/^(#|\*|\d{1,5}\. |\||> ).*?\n/mg, '')
    .replace(/\n/mg, '')
    .replace(/\*\*(.*?)\*\*/g, (m, $1) => $1)
    .replace(/__(.*?)__/g, (m, $1) => $1)
    .replace(/!\[(.*?)\]\(.*?\)/g, (m, $1) => $1)
    .replace(/\[(.*?)\]\(.*?\)/g, (m, $1) => $1)
    .replace(/"/g, '\\"')

  str = 100 <= str.length ? str.substring(0, 99) + '...' : str
  return str
}

/**
 * Create a header of article metadata.
 * @param {Object} metadata Metadata of article.
 * @param {String} markdown Markdown text of content body.
 * @return {String} Header text.
 */
const createMetadataHeader = (metadata, markdown) => {
  const last = metadata.type === 'page' ? 'single: true\n---\n\n' : '---\n\n'
  return `---
path: "/${metadata.type}s/${metadata.year}/${metadata.month}/${metadata.permanentName}/"
date: "${metadata.year}-${metadata.month}-${metadata.day}T${metadata.time}Z"
title: "${metadata.title}"
categories: ${arrayToString(metadata.categories)}
tags: ${arrayToString(metadata.tags)}
excerpt: "${createExcerpt(markdown)}"
${last}`
}

/**
 * Read an article metadata from xml object.
 * @param {Object} post XML object.
 * @return {Object} Metadata.
 */
const readMetadata = (post) => {
  const categories = []
  const tags       = []
  if (post.category) {
    post.category.forEach((value) => {
      switch (value.$.domain) {
        case 'category':
          categories.push(value._)
          break

        case 'post_tag':
          tags.push(value._)
          break

        default:
          break
      }
    })
  }

  const datetime = Util.datetimeFromWpGMT(post['wp:post_date_gmt'][0])
  return {
    year: datetime.year,
    month: datetime.month,
    day: datetime.day,
    time: datetime.time,
    permanentName: post['wp:post_name'][0],
    title: post['title'][0],
    categories,
    tags,
    type: post['wp:post_type'][0]
  }
}

/**
 * Replace the link URL included in Markdown.
 * @param {String} markdown Markdown text.
 * @param {String} oldPrefix Target.
 * @param {String} newPrefix String to replace.
 * @return {String} Replaced string.
 */
const replaceLinkURL = (markdown, oldPrefix, newPrefix) => {
  if (!(markdown && (oldPrefix && typeof oldPrefix === 'string') && (newPrefix && typeof newPrefix === 'string'))) {
    return markdown
  }

  return markdown.replace(/\[(.*?)\]\((.*?)\)/g, (match, $1, $2) => {
    const regexp = new RegExp(Util.escapeRegExp(oldPrefix), 'g')
    const url = $2.replace(regexp, newPrefix)
    return `[${$1}](${url})`
  })
}

/**
 * Convert the post data to markdown file.
 * @param {Object} post Post data.
 * @param {Object} metadata Metadata.
 * @param {String} rootDir Path of Markdown file output directory.
 * @param {Logger} logger Logger.
 * @param {CLIOptions} options Options.
 * @return {Promise} Promise task.
 */
const convertPost = async (post, metadata, rootDir, logger, options) => {
  logger.log(`${metadata.year}/${metadata.month}/${metadata.day} ['${metadata.type}']: ${metadata.title}`)

  const dir = createSaveDir(rootDir, metadata.year, metadata.month)
  if (!(dir)) {
    throw new Error('Failed to create a save directory.')
  }

  // If there are multiple articles on the same day, their names will be duplicated and made unique.
  const filePath = Util.uniquePathWithSequentialNumber(Path.join(dir, `${metadata.day}.md`))
  const stream = Fs.createWriteStream(filePath)
  if (!(stream)) {
    throw new Error('Failed to create the stream.')
  }

  let markdown = Convert(post['content:encoded'][0], options)
  if (options.withMetadata) {
    stream.write(createMetadataHeader(metadata, markdown), 'utf8')
  } else {
    stream.write(`# ${metadata.title}\n\n`, 'utf8')
  }

  if (options.withImageDownload) {
    const basename = Path.basename(filePath, '.md')
    markdown = await ImageLinkReplace(markdown, dir, basename, logger)
  }

  if (options.replaceLinkPrefix) {
    markdown = replaceLinkURL(markdown, options.replaceLinkPrefix.old, options.replaceLinkPrefix.new)
  }

  if (options.withComment) {
    markdown += Comment(post['wp:comment'])
  }

  stream.write(markdown, 'utf8')
}

/**
 * Create a directory with a unique name.
 * @param {String} dir Base directory path.
 * @return {String} The path of the created directory. Failure is null.
 */
const createUniqueDestDir = (dir) => {
  const base = Path.resolve(dir)
  const name = Util.formatDate(new Date(), 'YYYYMMDD-hhmmss')

  let path = Path.resolve(base, name)
  if (!(Util.existsSync(path))) {
    if (Util.mkdirSync(path)) {
      return path
    }
  }

  // Add sequential number
  for (let i = 1; i <= 256; ++i) {
    path = Path.resolve(base, name + '-' + i)
    if (!(Util.existsSync(path))) {
      if (Util.mkdirSync(path)) {
        return path
      }
    }
  }

  return null
}

/**
 * Gets the posts data from XML.
 * @param {String} src Path of XML file..
 * @return {Promise} Promise task.
 */
const postsFromXML = async (src) => {
  const data = Fs.readFileSync(Path.resolve(src))
  if (!(data)) {
    throw new Error(`"${src}" is not found.`)
  }
  const xml   = await ParseXML(data.toString())
  return xml.rss.channel[0].item
}

/**
 * Conver WordPress XML file to Markdown files.
 * @param {String} src Path of the WordPress XML file.
 * @param {String} dest Path of Markdown files output directory.
 * @param {CLIOptions} options Options.
 * @return {Promise} Promise object.
 */
const WordPressXmlToMarkdown = async (src, dest, options = { report: false }) => {
  const logger = new Logger(options.report)
  logger.log(`Input:  ${src}`)
  logger.log(`Output: ${dest}`)

  const dir = createUniqueDestDir(dest)
  if (!(dir)) {
    throw new Error('Failed to create the root directory.')
  }

  const postsDir = Path.join(dir, 'posts')
  if (!(Util.mkdirSync(postsDir))) {
    throw new Error('Failed to create the posts directory.')
  }

  const pagesDir = Path.join(dir, 'pages')
  if (!(Util.mkdirSync(pagesDir))) {
    throw new Error('Failed to create the pages directory.')
  }

  const posts = await postsFromXML(src)
  for (let post of posts) {
    const m = readMetadata(post)
    await convertPost(post, m, m.type === 'post' ? postsDir : pagesDir, logger, options)
  }
}

export default WordPressXmlToMarkdown

